import React from 'react';
import './App.css';
import styled from 'styled-components';
import Lista from './components/Lista'

const MainContainer = styled.div`
  margin: 0;
  padding: 0;
  display:flex;
  justify-content: center;
  `;

const EstiloH1 = styled.h1`
  color: #DC143C;
`;

const EstiloButton = styled.button`
  background-color: #DC143C;
  color: white;
`;

const EstiloLabel = styled.label`
  color: #DC143C;
`;

function App() {
  return (
    <div className="App">
      <MainContainer/>
      <EstiloH1> Lista de Tarefas </EstiloH1>
      <input
        type="text"
        placeholder="Insira sua tarefa"/> 
      <EstiloButton 
      onClick={this.onclickAdicionarTarefa}> 
      Adicionar tarefa 
      </EstiloButton>
      <br/> <br/>
      <EstiloLabel> Pesquisar </EstiloLabel>   
      <select>
        <option> Nenhum </option>  
        <option> Pendentes </option>
        <option> Finalizadas </option>
      </select>  
      <ListaAparecendo/> 
    </div>
  );
}

class ListaAparecendo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      listaAparecendo: false
    }
  }

onclickAdicionarTarefa = () => {
  this.setState({listaAparecendo: !this.state.listaAparecendo})
}

render() {
  return (
    <div>
      {this.state.listaAparecendo} && <Lista/>
    </div>
  );
}

}



export default App;
